mapper.select:
select bno, title, content, writer, reg_date
    from board
order by bno asc;

mapper.insert:
insert into board
    (title, content, writer, reg_date)
values (#{title}, #{content}, #{writer},#{reg_date} );

mapper.update:
update board
set  title = #{title},
     content = #{content},
     update = now();
where bno = #{bno};
